### Single call CCM encryption + authentication with plaintext CryptoKey in blocking return mode # #include<ti/drivers/AESCCM.h>
#include<ti/drivers/types/cryptoKey/CryptoKey_Plaintext.h>

...

AESCCM_Handlehandle;
CryptoKeycryptoKey;
int_fast16_tencryptionResult;
uint8_tnonce[]="Thisisanonce";
uint8_taad[]="Thisstringwillbeauthenticatedbutnotencrypted.";
uint8_tplaintext[]="Thisstringwillbeencryptedandauthenticated.";
uint8_tmac[16];
uint8_tciphertext[sizeof(plaintext)];
uint8_tkeyingMaterial[32]={0x00,0x01,0x02,0x03,0x04,0x05,0x06,0x07,
0x08,0x09,0x0A,0x0B,0x0C,0x0D,0x0E,0x0F,
0x10,0x11,0x12,0x13,0x14,0x15,0x16,0x17,
0x18,0x19,0x1A,0x1B,0x1C,0x1D,0x1E,0x1F}

handle=AESCCM_open(0,NULL);

if(handle==NULL){
//handleerror
}

CryptoKeyPlaintext_initKey(&cryptoKey,keyingMaterial,sizeof(keyingMaterial));

AESCCM_Operationoperation;
AESCCM_Operation_init(&operation);

operation.key=&cryptoKey;
operation.aad=aad;
operation.aadLength=sizeof(aad);
operation.input=plaintext;
operation.output=ciphertext;
operation.inputLength=sizeof(plaintext);
operation.nonce=nonce;
operation.nonceLength=sizeof(nonce);
operation.mac=mac;
operation.macLength=sizeof(mac);

encryptionResult=AESCCM_oneStepEncrypt(handle,&operation);

if(encryptionResult!=AESCCM_STATUS_SUCCESS){
//handleerror
}

AESCCM_close(handle);
